import userService from '../services/user'

const userReducer = (state, action) => {
  switch (action.type) {
    case 'INIT':
      return action.users
    default:
      return state
  }
}

export const initUsers = () => {
  return async (dispatch) => {
    userService.getAll().then(users =>
      dispatch({
        type: 'INIT',
        users
      })
    )
  }
}

export default userReducer
